<?phpfunction showList($link){    $list="";    foreach (glob('*.php') as $file){         $list.="<li>".$file."</li>\n";    }    return $list;}?><!DOCTYPE html><html><head>    <title>herhalingsopdracht 01</title>    <link href="stijl.css" rel="stylesheet" type="text/css"></head><body><h1>herhalingsopdracht 01</h1><h2>Pagina index</h2><a href="index.php?link=cursus">Cursus</a><a href="index.php?link=voorbeelden">Voorbeelden</a><a href="index.php?link=opdrachten">Opdrachten</a><form action="index.php" method="get">    <label for="zoekveld">Zoeken naar: </label>    <input type="search" placeholder="Geef een zoekterm in" name="zoeknaar" id="zoekveld">    <input type="submit" value="zoek" name="zoek"></form><h2>Inhoud</h2><?php if(isset($_GET['link'])):?><?php switch($_GET['link']):case "cursus":?>    <iframe src="http://web-backend.local/cursus/web-backend-cursus.pdf"></iframe><?php break;?><?php case "voorbeelden":?>    <ul>    <?php echo showList("http://web-backend.local/cursus/voorbeelden/");?>    </ul><?php break;?><?php case "opdrachten":?>    <iframe src="http://web-backend.local/cursus/opdrachten/"></iframe><?php default:?><?php break;?><?php endswitch;?><?php endif;?></body></html>